import groovy.json.JsonSlurper 

def responseContent = messageExchange.getResponseContent() 

def slurper = new JsonSlurper()
def json = slurper.parseText responseContent

assert json.data.id==12
log.info json.data.categoryImageUrl.id
//assert json.data.categoryImageUrl.id==199)

def idList= json.data.categoryImageUrl.id
log.info idList.getClass()
def a=idList.find{
	it==199
}

log.info a

assert a==199
