con:configuration.scriptText:
import groovy.json.JsonSlurper 

def responseContent = messageExchange.getResponseContent() 

log.info responseContent

def slurper = new JsonSlurper()
def json = slurper.parseText responseContent

def idList= json.data.id

log.info idList.getClass()
def id=idList.find{
	it=="2c9280855851972b0158519761140002"
}

log.info id
assert id.equals("2c9280855851972b0158519761140002")

