con:configuration.scriptText:
import groovy.json.JsonSlurper 

def responseContent = messageExchange.getResponseContent() 

log.info responseContent

def slurper = new JsonSlurper()
def json = slurper.parseText responseContent

def experienceList= json.data.experienceExtranetId

log.info experienceList.getClass()
def exp=experienceList.find{
	it=="2c92808558518bfc0158518c36400002"
}

log.info exp

assert exp.equals("2c92808558518bfc0158518c36400002")

def RefCodeList= json.data.referenceCode

log.info RefCodeList.getClass()
def refcode=RefCodeList.find{
	it=="DEL-RED-102-3"
}

log.info refcode
assert refcode=="DEL-RED-102-3"
