import groovy.json.JsonSlurper 

def responseContent = messageExchange.getResponseContent() 

log.info responseContent

def slurper = new JsonSlurper()
def json = slurper.parseText responseContent
def id= json.id
log.info id.getClass()
def a=id.find{
	it==1
}

log.info a

assert a==1

