import groovy.json.JsonSlurper 

def responseContent = messageExchange.getResponseContent() 

log.info responseContent

def slurper = new JsonSlurper()
def json = slurper.parseText responseContent

log.info json.cities.id

def citiesID = json.cities.id
log.info citiesID.getClass()
def a=citiesID.find{
	it==6
}

log.info a

assert a==6

log.info json.cities*.countryId

def CountryID = json.cities*.countryId
log.info CountryID.getClass()
def Country=CountryID.find{
	it==3
}
assert Country==3

import groovy.json.JsonSlurper 

def responseContent = messageExchange.getResponseContent() 

log.info responseContent

def slurper = new JsonSlurper()
def json = slurper.parseText responseContent

